CREATE TABLE cart (id BIGINT AUTO_INCREMENT, client_id BIGINT NOT NULL, restaurant_id BIGINT NOT NULL, product_id TEXT NOT NULL, status TINYINT(1) DEFAULT '0' NOT NULL, total FLOAT(18, 2) NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX restaurant_id_idx (restaurant_id), INDEX client_id_idx (client_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE category (id BIGINT AUTO_INCREMENT, restaurant_id BIGINT NOT NULL, name VARCHAR(255) NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX restaurant_id_idx (restaurant_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE cities (id BIGINT AUTO_INCREMENT, nom VARCHAR(255) NOT NULL, cp VARCHAR(255) NOT NULL, latitude FLOAT(18, 2) NOT NULL, longitude FLOAT(18, 2) NOT NULL, eloignement VARCHAR(255) NOT NULL, url VARCHAR(255) NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE comments (id BIGINT AUTO_INCREMENT, text VARCHAR(255) NOT NULL, member_id BIGINT NOT NULL, restaurant_id BIGINT NOT NULL, note BIGINT NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX member_id_idx (member_id), INDEX restaurant_id_idx (restaurant_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE deals (id BIGINT AUTO_INCREMENT, image VARCHAR(255), description TEXT NOT NULL, value BIGINT NOT NULL, start DATE NOT NULL, end DATE NOT NULL, restaurant_id BIGINT NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX restaurant_id_idx (restaurant_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE detail (id BIGINT AUTO_INCREMENT, size VARCHAR(255) NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE entity (id BIGINT AUTO_INCREMENT, sf_guard_user_id BIGINT NOT NULL, last_name VARCHAR(255) NOT NULL, first_name VARCHAR(255) NOT NULL, address TEXT NOT NULL, phone VARCHAR(255) NOT NULL, image VARCHAR(255), created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX sf_guard_user_id_idx (sf_guard_user_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE product (id BIGINT AUTO_INCREMENT, image VARCHAR(255), restaurant_id BIGINT NOT NULL, category_id BIGINT NOT NULL, detail_id BIGINT, name VARCHAR(255) NOT NULL, description TEXT NOT NULL, price FLOAT(18, 2) NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX restaurant_id_idx (restaurant_id), INDEX category_id_idx (category_id), INDEX detail_id_idx (detail_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE restaurant (id BIGINT AUTO_INCREMENT, entity_id BIGINT NOT NULL, image VARCHAR(255), name VARCHAR(255) NOT NULL, address VARCHAR(255), city VARCHAR(255), postal_code VARCHAR(5), type VARCHAR(255), phone VARCHAR(255), description TEXT, open_time TIME, close_time TIME, status TINYINT(1) DEFAULT '0' NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, slug VARCHAR(255), UNIQUE INDEX restaurant_sluggable_idx (slug), INDEX entity_id_idx (entity_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE sf_guard_forgot_password (id BIGINT AUTO_INCREMENT, user_id BIGINT NOT NULL, unique_key VARCHAR(255), expires_at DATETIME NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX user_id_idx (user_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE sf_guard_group (id BIGINT AUTO_INCREMENT, name VARCHAR(255) UNIQUE, description TEXT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE sf_guard_group_permission (group_id BIGINT, permission_id BIGINT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(group_id, permission_id)) ENGINE = INNODB;
CREATE TABLE sf_guard_permission (id BIGINT AUTO_INCREMENT, name VARCHAR(255) UNIQUE, description TEXT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE sf_guard_remember_key (id BIGINT AUTO_INCREMENT, user_id BIGINT, remember_key VARCHAR(32), ip_address VARCHAR(50), created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX user_id_idx (user_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE sf_guard_user (id BIGINT AUTO_INCREMENT, first_name VARCHAR(255), last_name VARCHAR(255), email_address VARCHAR(255) NOT NULL UNIQUE, username VARCHAR(128) NOT NULL UNIQUE, algorithm VARCHAR(128) DEFAULT 'sha1' NOT NULL, salt VARCHAR(128), password VARCHAR(128), is_active TINYINT(1) DEFAULT '1', is_super_admin TINYINT(1) DEFAULT '0', last_login DATETIME, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX is_active_idx_idx (is_active), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE sf_guard_user_group (user_id BIGINT, group_id BIGINT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(user_id, group_id)) ENGINE = INNODB;
CREATE TABLE sf_guard_user_permission (user_id BIGINT, permission_id BIGINT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(user_id, permission_id)) ENGINE = INNODB;
ALTER TABLE cart ADD CONSTRAINT cart_restaurant_id_restaurant_id FOREIGN KEY (restaurant_id) REFERENCES restaurant(id) ON DELETE CASCADE;
ALTER TABLE cart ADD CONSTRAINT cart_client_id_entity_id FOREIGN KEY (client_id) REFERENCES entity(id) ON DELETE CASCADE;
ALTER TABLE category ADD CONSTRAINT category_restaurant_id_restaurant_id FOREIGN KEY (restaurant_id) REFERENCES restaurant(id) ON DELETE CASCADE;
ALTER TABLE comments ADD CONSTRAINT comments_restaurant_id_restaurant_id FOREIGN KEY (restaurant_id) REFERENCES restaurant(id) ON DELETE CASCADE;
ALTER TABLE comments ADD CONSTRAINT comments_member_id_entity_id FOREIGN KEY (member_id) REFERENCES entity(id) ON DELETE CASCADE;
ALTER TABLE deals ADD CONSTRAINT deals_restaurant_id_restaurant_id FOREIGN KEY (restaurant_id) REFERENCES restaurant(id) ON DELETE CASCADE;
ALTER TABLE entity ADD CONSTRAINT entity_sf_guard_user_id_entity_id FOREIGN KEY (sf_guard_user_id) REFERENCES entity(id) ON DELETE CASCADE;
ALTER TABLE product ADD CONSTRAINT product_restaurant_id_restaurant_id FOREIGN KEY (restaurant_id) REFERENCES restaurant(id) ON DELETE CASCADE;
ALTER TABLE product ADD CONSTRAINT product_detail_id_detail_id FOREIGN KEY (detail_id) REFERENCES detail(id) ON DELETE CASCADE;
ALTER TABLE product ADD CONSTRAINT product_category_id_category_id FOREIGN KEY (category_id) REFERENCES category(id) ON DELETE CASCADE;
ALTER TABLE restaurant ADD CONSTRAINT restaurant_entity_id_entity_id FOREIGN KEY (entity_id) REFERENCES entity(id) ON DELETE CASCADE;
ALTER TABLE sf_guard_forgot_password ADD CONSTRAINT sf_guard_forgot_password_user_id_sf_guard_user_id FOREIGN KEY (user_id) REFERENCES sf_guard_user(id) ON DELETE CASCADE;
ALTER TABLE sf_guard_group_permission ADD CONSTRAINT sf_guard_group_permission_permission_id_sf_guard_permission_id FOREIGN KEY (permission_id) REFERENCES sf_guard_permission(id) ON DELETE CASCADE;
ALTER TABLE sf_guard_group_permission ADD CONSTRAINT sf_guard_group_permission_group_id_sf_guard_group_id FOREIGN KEY (group_id) REFERENCES sf_guard_group(id) ON DELETE CASCADE;
ALTER TABLE sf_guard_remember_key ADD CONSTRAINT sf_guard_remember_key_user_id_sf_guard_user_id FOREIGN KEY (user_id) REFERENCES sf_guard_user(id) ON DELETE CASCADE;
ALTER TABLE sf_guard_user_group ADD CONSTRAINT sf_guard_user_group_user_id_sf_guard_user_id FOREIGN KEY (user_id) REFERENCES sf_guard_user(id) ON DELETE CASCADE;
ALTER TABLE sf_guard_user_group ADD CONSTRAINT sf_guard_user_group_group_id_sf_guard_group_id FOREIGN KEY (group_id) REFERENCES sf_guard_group(id) ON DELETE CASCADE;
ALTER TABLE sf_guard_user_permission ADD CONSTRAINT sf_guard_user_permission_user_id_sf_guard_user_id FOREIGN KEY (user_id) REFERENCES sf_guard_user(id) ON DELETE CASCADE;
ALTER TABLE sf_guard_user_permission ADD CONSTRAINT sf_guard_user_permission_permission_id_sf_guard_permission_id FOREIGN KEY (permission_id) REFERENCES sf_guard_permission(id) ON DELETE CASCADE;
